#tbl_Portfolios {
    width: 100%;
}

#tbl_Portfolios tbody tr {
    cursor: pointer;
}

#tbl_Portfolios tbody th div {
    max-width: 200px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

/*#region Post Card*/
.card {
    max-width: 16rem;
    margin: 10px 0;
}

.card .card-body p {
    overflow: hidden;
    text-overflow: ellipsis;
    /*white-space: nowrap;*/
    max-height: 60px;
    width: 198px;
}

/*#endregion*/

/*#region Custom Checkbox*/
.label-checkbox {
    display: block;
    position: relative;
    padding-left: 35px;
    margin-bottom: 12px;
    cursor: pointer;
    font-size: 22px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

.span-checkbox {
    position: absolute;
    top: 0;
    left: 0;
    height: 25px;
    width: 25px;
    background-color: #eee;
}

.label-checkbox:hover ~ .span-checkbox {
    background: #ccc;
}

.label-checkbox input:checked ~ .span-checkbox {
    background-color: #2196f3;
}

.label-checkbox input:checked ~ .span-checkbox:after {
    display: block;
}

.span-checkbox:after {
    content: "";
    position: absolute;
    display: none;
    left: 8px;
    top: 4px;
    width: 8px;
    height: 14px;
    border: solid white;
    border-width: 0 3px 3px 0;
    -webkit-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    transform: rotate(45deg);
}

/*#endregion*/

/*#region Draggable*/
.fa-arrows-h {
    font-size: 25pt;
}

.fa-arrows-h:hover {
    cursor: pointer;
}

.position-fixed {
    top: 0;
    left: 0;
    width: 100%;
    z-index: 999;
    margin: 0;
    background-color: white;
}

.item {
    background-color: #ccc;
    padding: 5px 10px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    width: 100px;
    margin: 15px 5px 15px 10px;
}

.overflow-x-scroll {
    overflow-x: auto;
}

.sortable-ghost {
    opacity: 0.5;
}

.sortable-drag {
    opacity: 0;
}

.sortable-chosen {
    opacity: 0.5;
}

/*#endregion*/

@media (max-width: 576px) {
    .student-image {
        max-width: 94px;
    }
}

@media (max-width: 768px) {
    .student-image {
        max-width: 120px;
    }
}

.div_PortfolioPosts {
    max-height: 500px;
    overflow-y: scroll;
}
