        isImage(obj_File) {
            let ext = String(obj_File.UPPImageFileExt).toUpperCase();
            let isImage = false;

            switch (ext) {
                case ".PNG":
                case ".JPG":
                case ".BMP":
                case ".GIF":
                    isImage = true;
                    break;
                default:
                    isImage = false;
                    break;
            }
